import React, { useState, useEffect } from 'react';

function SearchBar({ onSearch }) {
  const [searchText, setSearchText] = useState('');
  const [isInputFocused, setIsInputFocused] = useState(false);
  const [showSearchResults, setShowSearchResults] = useState(false);

  useEffect(() => {
    setShowSearchResults(searchText.trim() !== '' && !isInputFocused); // Only show if searchText is not empty and input is not focused
  }, [searchText, isInputFocused]);

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchText(inputValue);
    setIsInputFocused(true); // Always set focus when typing
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(searchText);
  };

  return (
    <div className="search-bar" onClick={() => setIsInputFocused(true)}>
      {/* Conditionally render the input field based on isInputFocused state */}
      {isInputFocused ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={searchText}
            onChange={handleChange}
            onBlur={() => setIsInputFocused(false)}
            placeholder="Search..."
            autoFocus // Autofocus the input field when it's rendered
          />
        </form>
      ) : (
        <span>Search...</span>
      )}
      {/* Conditionally render search results */}
      {showSearchResults && (
        <div className="search-results">
          {/* Render search results here */}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
