import React, { useState, useEffect } from 'react';
import "./searchBar.css";

function SearchBar({ onSearch, onClear }) { // Add onClear function as prop
  const [searchText, setSearchText] = useState('');
  const [isInputFocused, setIsInputFocused] = useState(false);
  const [showSearchResults, setShowSearchResults] = useState(false);

  const handleInputFocus = () => {
    setIsInputFocused(true);
  };

  const handleInputBlur = () => {
    setIsInputFocused(false);
  };

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchText(inputValue);
    onSearch(inputValue);
  };

  useEffect(() => {
    setShowSearchResults(searchText.trim() !== '' && isInputFocused);
  }, [searchText, isInputFocused]);

  const handleClearSearch = () => {
    setSearchText('');
    setShowSearchResults(false);
    onClear();
  };

  // Event handler for the navigation button to clear search text and trigger navigation
  const handleNavigationButtonClick = () => {
    handleClearSearch();
    // Add navigation action here
    console.log("Navigation button clicked");
  };

  return (
    <div className="search-bar" onClick={handleInputFocus}>
      {isInputFocused ? (
        <form onSubmit={(e) => e.preventDefault()}>
          <div className="input-wrapper">
            <input
              type="text"
              value={searchText}
              onChange={handleChange}
              onBlur={handleInputBlur}
              placeholder="Search..."
              autoFocus
            />
          </div>
        </form>
      ) : (
        null
      )}
      {showSearchResults && (
        <div className="search-results">
          {/* Render search results here */}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
