import React, { useState, useEffect } from 'react';

function SearchBar({ onSearch }) {
  const [searchText, setSearchText] = useState('');
  const [isInputFocused, setIsInputFocused] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    if (!searchText && !isInputFocused) {
      setSearchResults([]); // Clear search results if input is empty and not focused
    }
  }, [searchText, isInputFocused]);

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchText(inputValue);
    setIsInputFocused(true); // Always set focus when typing
    onSearch(inputValue); // Trigger search on input change
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsInputFocused(false); // Remove focus on submit
  };

  return (
    <div className="search-bar" onClick={() => setIsInputFocused(true)}>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={searchText}
          onChange={handleChange}
          onBlur={() => setIsInputFocused(false)}
          placeholder="Search..."
          autoFocus // Autofocus the input field when it's rendered
        />
      </form>
      {/* Conditionally render search results */}
      {searchResults.length > 0 && (
        <div className="search-results">
          {searchResults.map((result, index) => (
            <div key={index}>{result}</div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
