import React, { useState, useEffect } from 'react';
import "./Transcript.css"

const Transcript = ({ text, audioRef }) => {
  const [highlightedIndex, setHighlightedIndex] = useState(0);

  useEffect(() => {
    if (!audioRef.current) return;

    const handleTimeUpdate = () => {
      const currentTime = audioRef.current.currentTime;
      console.log('Current Time:', currentTime);
      const index = findHighlightedIndex(currentTime);
      console.log('Highlighted Index:', index); // Add console log for highlighted index
      setHighlightedIndex(index);
    };

    const findHighlightedIndex = (currentTime) => {
      const durationPerSentence = 9; // Assuming each sentence lasts for 5 seconds
      return Math.floor(currentTime / durationPerSentence);
    };

    audioRef.current.addEventListener('timeupdate', handleTimeUpdate);

    return () => {
      audioRef.current.removeEventListener('timeupdate', handleTimeUpdate);
    };
  }, [audioRef]);

  return (
    <div className="transcript">
      {text.map((sentence, index) => (
        <p key={index} className={index === highlightedIndex ? 'highlighted' : ''}>
          {sentence}
        </p>
      ))}
    </div>
  );
};

export default Transcript;
