// App.js
import React, { useState } from 'react';
import './App.test';
import Header from './Component/Header/Header';
import Home from './Component/Main/Home/Home';
import Schedule from './Component/Main/Schedule/Schedule';
import Program from './Component/Main/Program/Program';
import Events from './Component/Main/UpcomingEvents/upcomingEvents';
import ContactUs from './Component/Main/ContactUs/ContactUs';
import Merchandise from './Component/Main/Merchandise/Merchandise'; 
import News from './Component/Main/News/News';
import Footer from './Component/Footer/Footer';
import ScrollToTopButton from './Component/Main/ScrollToTop/ScrollToTop';
import SearchBar from './Component/Main/searchBar/searchBar'; // Correct import for SearchBar
import SearchLogic from './Component/Main/searchBar/searchLogic'; // Correct import for SearchLogic
import Weather from './Component/Main/Weather/Weather';

function App() {
  const [searchText, setSearchText] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async (query) => {
    setSearchText(query);
    try {
      // Simulating search API call
      const response = await fetch(`your-search-api-endpoint?q=${query}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
      setSearchResults([]);
    }
  };

  const handleClose = () => {
    setSearchText('');
    setSearchResults([]);
  };

  return (
    <div className="App">
      <Header searchResults={searchResults} />
      <Home />
      <Schedule />
      <Program />
      <Events />
      <News />
      <Weather />
      <Merchandise />
      <ContactUs />
      <ScrollToTopButton />
      <SearchBar onSearch={handleSearch} onClose={handleClose} searchResults={searchResults} />
      <SearchLogic searchText={searchText} />
    </div>
  );
}

export default App;
