import React, { useState } from 'react';
import DataTable from '../DataTable/DataTable';
import scheduleDataJson from '../Schedule/scheduleData.json';
import './Schedule.css';

function Schedule() {
  const [search, setSearch] = useState('');
  const filteredData = scheduleDataJson.filter((item) => {
    const columns = Object.values(item).map((value) =>
      value.toString().toLowerCase()
    );
    return columns.some((column) => column.includes(search.toLowerCase()));
  });

  return (
    <section id="schedule">
      <h1>Weekly Schedule</h1>
      <div className="schedule-container">
        <input
          type="text"
          placeholder="Search by Day, Time, or Event"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="search-input"
        />
        <DataTable data={filteredData} />
      </div>
    </section>
  );
}

export default Schedule;
