import React, { useState } from 'react';
import logo from '../../assets/images/Logo.png';
import './Header.css';
import SearchLogic from './SearchLogic'; // Import SearchLogic component

function Header() {
  // Initialize searchResults state
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchText, setSearchText] = useState('');

  // Function to handle search logic and update searchResults state
  const handleSearch = (searchText) => {
    // Perform search logic here and update searchResults state
    const results = performSearch(searchText); // Example: Perform search logic
    setSearchResults(results);
  };

  const toggleSearch = () => {
    setIsSearchOpen(!isSearchOpen);
  };

  return (
    <header>
      <nav id="navbar">
        <ul>
          <li><img src={logo} alt="Logo" /></li>
          <li><a href="#home">Home</a></li>
          <li><a href="#schedule">Schedule</a></li>
          <li><a href="#program">Programs</a></li>
          <li><a href="#events">Events</a></li>
          <li><a href="#news">News</a></li>
          <li><a href="#weather">Weather</a></li>
          <li><a href="#merch">Merch</a></li>
          <li><a href="#contactus">ContactUs</a></li>
          <li>
            <div className="search-wrapper">
              {isSearchOpen && <SearchLogic setSearchText={setSearchText} />}
              <input
                type="text"
                placeholder="Search..."
                onFocus={toggleSearch}
                onBlur={toggleSearch}
                onChange={(e) => {
                  setSearchText(e.target.value);
                  handleSearch(e.target.value);
                }}
              />
            </div>
          </li>
        </ul>
      </nav>
      {isSearchOpen && <SearchLogic searchResults={searchResults} />}
    </header>
  );
}

export default Header;
