import React, { useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Banner1 from '../../../assets/images/Banner1.png';
import Banner2 from '../../../assets/images/Banner2.png';
import Banner3 from '../../../assets/images/Banner3.png';
import './Home.css';
import ErrorBoundary from '../../errorBoundary'; // Import Error Boundary

function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    initialSlide: 0,
    beforeChange: (oldIndex, newIndex) => setCurrentSlide(newIndex),
  };

  const handleDotClick = (index) => {
    setCurrentSlide(index);
  };

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 2 ? 0 : prevSlide + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? 2 : prevSlide - 1));
  };

  return (
    <ErrorBoundary>
      <section id="home" className="section-margintop">
        <div className="carousel-wrapper">
          <Slider {...settings}>
            <div className="carousel-item">
              <img src={Banner1} alt="Image1" />
            </div>
            <div className="carousel-item">
              <img src={Banner2} alt="Image2" />
            </div>
            <div className="carousel-item">
              <img src={Banner3} alt="Image3" />
            </div>
          </Slider>
          <button id="prevBtn" className="prev" onClick={prevSlide}>
            &#10094;
          </button>
          <button id="nextBtn" className="next" onClick={nextSlide}>
            &#10095;
          </button>
        </div>

        <div className="dot-buttons">
          {[...Array(3)].map((_, index) => (
            <button
              key={index}
              onClick={() => handleDotClick(index)}
              className={currentSlide === index ? 'active' : ''}
            ></button>
          ))}
        </div>
      </section>
    </ErrorBoundary>
  );
}

export default Home;
