import React, { useState, useEffect } from 'react';
import scheduleDataJson from '../Schedule/scheduleData.json';

function Schedule() {
  const [scheduleData, setScheduleData] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    setScheduleData(scheduleDataJson);
  }, []); // Add scheduleDataJson as a dependency

  return (
    <section id="schedule">
      <h1>Weekly Schedule</h1>
      <input
        type="text"
        placeholder="Search by day"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      {scheduleData.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Days</th>
              <th>Time</th>
              <th>Event</th>
            </tr>
          </thead>
          <tbody>
            {scheduleData
              .filter((item) => {
                const columns = Object.values(item).map((value) =>
                  value.toString().toLowerCase()
                );
                return columns.some((column) =>
                  column.includes(search.toLowerCase())
                );
              })
              .map((scheduleItem, index) => (
                <tr key={index}>
                  <td>{scheduleItem.day}</td>
                  <td>{scheduleItem.time}</td>
                  <td>{scheduleItem.event}</td>
                </tr>
              ))}
          </tbody>
        </table>
      ) : (
        <p>No schedule data available</p>
      )}
    </section>
  );
}

export default Schedule;
