import React, { useState } from 'react';
import './Program.css';
import Program1 from '../../../assets/images/Program1.jpg';
import Program2 from '../../../assets/images/Program2.jpg';
import ErrorBoundary from '../../errorBoundary';

function Programs() {
  // State to store the search text
  const [searchText, setSearchText] = useState('');

  // Array of programs
  const programs = [
    {
      title: 'Season Kick-off Tournament',
      description: 'Welcome to the Elite Hoops Club tournament...',
      image: Program1
    },
    {
      title: 'Basketball Camps',
      description: 'Elite Hoops Club also known as the best basketball club...',
      image: Program2
    },
    // Add more programs as needed
  ];

  // Filter programs based on search text
  const filteredPrograms = programs.filter(program =>
    program.title.toLowerCase().includes(searchText.toLowerCase())
  );

  // Function to handle search text change
  const handleSearchTextChange = (e) => {
    setSearchText(e.target.value);
  };

  return (
    <ErrorBoundary>
      <section id="program">
        <h1>Programs</h1>
        {/* Search input */}
        <input
          type="text"
          value={searchText}
          onChange={handleSearchTextChange}
          placeholder="Search Programs..."
        />

        {/* Display filtered programs */}
        {filteredPrograms.map((program, index) => (
          <div key={index} className="program-container">
            <div className="program-content">
              <h2>{program.title}</h2>
              <p className="left-aligned">{program.description}</p>
            </div>
            <div className="program-image">
              <img src={program.image} alt={program.title} />
            </div>
          </div>
        ))}
      </section>
    </ErrorBoundary>
  );
}

export default Programs;
