import React, { useState } from 'react';
import logo from '../../assets/images/Logo.png';
import './Header.css';
import SearchLogic from './searchBar/searchLogic'; // Import SearchLogic component
import Search from './searchBar/searchBar'; // Import Search component

function Header({ searchResults, setSearchText, handleSearch }) {
  const [isSearchOpen, setIsSearchOpen] = useState(false);

  const handleInputFocus = () => {
    setIsSearchOpen(true);
    console.log("Input focused. Is search open?", isSearchOpen);
  };

  const handleInputBlur = () => {
    console.log("Search results on blur:", searchResults);
    if (!searchResults.length) {
      setIsSearchOpen(false);
      console.log("Input blurred. Is search open?", isSearchOpen);
    }
  };

  return (
    <header>
      <nav id="navbar">
        {/* Navigation items */}
      </nav>
      <div className="search-wrapper">
        {isSearchOpen && <Search onSearch={handleSearch} />}
        <input
          type="text"
          placeholder="Search..."
          onFocus={handleInputFocus}
          onBlur={handleInputBlur}
          onChange={(e) => {
            const inputValue = e.target.value;
            console.log("Input Value:", inputValue);
            handleSearch(inputValue);
          }}
        />
      </div>
      {/* Render the SearchLogic component below the search bar */}
      <SearchLogic searchResults={searchResults} />
    </header>
  );
}


export default Header;
