import React, { useState, useEffect } from 'react';
import logo from '../../../assets/images/Logo.png';
import './Navigation.css';
import SearchLogic from '../searchBar/searchLogic'; // Import SearchLogic component

function Header({ searchResults, setSearchResults, handleSearch }) {
  const [searchText, setSearchText] = useState('');
  const [isSearchOpen, setIsSearchOpen] = useState(false); // Add isSearchOpen state variable

  const handleInputFocus = () => {
    setIsSearchOpen(true);
  };

  const handleInputBlur = () => {
    setIsSearchOpen(false); // Always set isSearchOpen to false on blur
  };

  const handleClearSearch = () => {
    setSearchText('');
    setSearchResults([]);
  };

  // Event handler for the navigation button to clear search text and trigger navigation
  const handleNavigationButtonClick = () => {
    console.log("Navigation button clicked"); // Log message to check if button click is detected
    handleClearSearch(); // Clear the search text
    // Add navigation action here
  };

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchText(inputValue);
    handleSearch(inputValue);
  };

  useEffect(() => {
    setIsSearchOpen(searchText.trim() !== '');
  }, [searchText]);

  return (
    <header>
      <nav id="navbar">
        <ul>
          <li><img src={logo} alt="Logo" /></li>
          <li><a href="#home">Home</a></li>
          <li><a href="#schedule">Schedule</a></li>
          <li><a href="#program">Programs</a></li>
          <li><a href="#events">Events</a></li>
          <li><a href="#news">News</a></li>
          <li><a href="#weather">Weather</a></li>
          <li><a href="#merch">Merch</a></li>
          <li><a href="#contactus">ContactUs</a></li>
          <li>
            <div className="search-wrapper">
              {/* Render the Search component conditionally based on isSearchOpen */}
              {isSearchOpen && (
                <div className="search-bar">
                  <form onSubmit={(e) => e.preventDefault()}>
                    <div className="input-wrapper">
                      <input
                        type="text"
                        value={searchText}
                        onChange={handleChange}
                        onBlur={handleInputBlur}
                        placeholder="Search..."
                        autoFocus
                      />
                      {/* Change the button text to "X" */}
                      <button type="button" onClick={handleClearSearch}>X</button>
                    </div>
                  </form>
                </div>
              )}
              {!isSearchOpen && (
                <input
                  type="text"
                  placeholder="Search..."
                  onFocus={handleInputFocus}
                  onBlur={handleInputBlur}
                  value={searchText}
                  onChange={handleChange}
                />
              )}
              {/* Use the navigation button to clear search text and trigger navigation */}
              <button onClick={handleNavigationButtonClick}>NavigationButton</button>
            </div>
          </li>
        </ul>
      </nav>
      {/* Render the SearchLogic component below the search bar */}
      <SearchLogic searchResults={searchResults} />
    </header>
  );
}

export default Header;
