import React from 'react';
import './Program.css'; // Import the CSS file
import Program1 from '../../../assets/images/Program1.jpg';
import Program2 from '../../../assets/images/Program2.jpg';
import ErrorBoundary from '../../errorBoundary'; // Import Error Boundary
import SearchLogic from '../searchBar/searchLogic';

function Programs({ searchResults }) {

  const programData = [
    {
      title: "Season Kick-off Tournament",
      description: "Welcome to the Elite Hoops Club tournament, we are known for our Slam Dunks. We are announcing our Season kick-off tournament at the start of April. This is gonna be one of the best tournaments that this club has ever organized as we will have lots of celebrity guests like Justin Bieber and Michael Jordan. Please come and join us in this Season Tournament.",
      image: Program1
    },
    {
      title: "Basketball Camps",
      description: "Elite Hoops Club also known as the best basketball club while Vic is announcing their Basketball Club. Please Join us in this Adventure with Daily Classes available for a wide range of club member levels. This year we will be having lots of amazing coaches like Larry Brown, Chuck Daly, and Jerry Sloan.",
      image: Program2
    }
  ];

  return (
    <ErrorBoundary>
      <section id="program">
        <h1>Programs</h1>
        {/* Display search results */}
        <SearchLogic searchText={searchText} data={searchResults} />
        {/* Display all programs */}
        {programData.map((program, index) => (
          <div className="program-container" key={index}>
            {/* Program content */}
            <div className="program-content">
              <h2>{program.title}</h2>
              <p className="left-aligned">{program.description}</p>
            </div>
            {/* Program image */}
            <div className="program-image">
              <img src={program.image} alt={program.title} />
            </div>
          </div>
        ))}
      </section>
    </ErrorBoundary>
  );
}

export default Programs;
