import React, { useState, useEffect } from 'react';
import "./searchBar.css";

function SearchBar({ onSearch, onClear }) {
  const [searchText, setSearchText] = useState('');
  const [isInputFocused, setIsInputFocused] = useState(false);
  const [showSearchResults, setShowSearchResults] = useState(false);

  const handleInputFocus = () => {
    setIsInputFocused(true);
  };

  const handleInputBlur = () => {
    setIsInputFocused(false);
  };

  const handleChange = (e) => {
    const inputValue = e.target.value;
    setSearchText(inputValue);
    onSearch(inputValue);
  };

  const handleClear = () => {
    setSearchText('');
    setShowSearchResults(false);
    onClear(); // Call the onClear function passed from the parent component
  };

  useEffect(() => {
    setShowSearchResults(searchText.trim() !== '' && isInputFocused);
  }, [searchText, isInputFocused]);

  return (
    <div className="search-bar" onClick={handleInputFocus}>
      {isInputFocused ? (
        <form onSubmit={(e) => e.preventDefault()}>
          <input
            type="text"
            value={searchText}
            onChange={handleChange}
            onBlur={handleInputBlur}
            placeholder="Search..."
            autoFocus
          />
          <button type="button" onClick={handleClear}>
            &#10006; {/* Unicode character for cross */}
          </button>
        </form>
      ) : (
        <span>Search...</span>
      )}
      {showSearchResults && (
        <div className="search-results">
          {/* Render search results here */}
        </div>
      )}
    </div>
  );
}

export default SearchBar;
