import React, { useState } from 'react';
import logo from '../../assets/images/Logo.png';
import './Header.css';
import SearchLogic from './searchBar/searchLogic'; // Import SearchLogic component
import Search from './searchBar/searchBar'; // Import Search component

function Header({ searchResults, setSearchText, handleSearch }) {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [query, setQuery] = useState('');

  const toggleSearch = () => {
    setIsSearchOpen(!isSearchOpen);
  };

  const handleChange = (event) => {
    setQuery(event.target.value);
    setSearchText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    handleSearch(query);
  };

  return (
    <header>
      <nav id="navbar">
        <ul>
          <li><img src={logo} alt="Logo" /></li>
          <li><a href="#home">Home</a></li>
          <li><a href="#schedule">Schedule</a></li>
          <li><a href="#program">Programs</a></li>
          <li><a href="#events">Events</a></li>
          <li><a href="#news">News</a></li>
          <li><a href="#weather">Weather</a></li>
          <li><a href="#merch">Merch</a></li>
          <li><a href="#contactus">ContactUs</a></li>
          <li>
            <div className="search-wrapper">
              {isSearchOpen && <SearchLogic searchResults={searchResults} />}
              <form onSubmit={handleSubmit}>
                <input
                  type="text"
                  placeholder="Search..."
                  onFocus={toggleSearch}
                  onBlur={toggleSearch}
                  onChange={handleChange}
                  value={query}
                />
                <button type="submit">Search</button>
              </form>
            </div>
          </li>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
